from math import pi


def welcome():
    print('Hello, welcome to area calculator package!')


def add(num1: int, num2: int) -> int:
    return num1 + num2


def subtract(num1: int, num2: int) -> int:
    return num1 - num2


def multiply(num1: int, num2: int) -> int:
    return num1 * num2


def square(a):
    return a * a
